	// 		requestAnimationFrame(animate);
	// 		mesh.rotation.y += 0.01;
	// 		mesh.rotation.x += 0.01;
	// 		renderer.setSize(window.innerWidth, window.innerHeight);
	// 		renderer.render(scene, camera);
	// 	};
	// 	animate();
	// }, []);

	return (
    <div className="skill" id="skill">
      <canvas className="homeCanvas" />
        <Container className="mySkill">
        <Row className="">
			<Row>
				<h3 className="title">Used by My Hands And Mind</h3>
			</Row>
			<Row className="skill-content">
				<Col className="comp col-lg-4 ">
					<img src={require('../assets/comp2.jpeg')} alt="" className="comp" />
				</Col>
				<Col className="lang-row">
					<Row className="">
								<Col className="lang-col">
									<h6>Languages</h6>
									<ul >
										<li>HTML</li>
										<li>CSS</li>
										<li>Javascript</li>
										<li>C</li>
										<li>Python</li>
										<li>Java</li>
										<li>PHP</li>
									</ul>
								</Col>
								<Col className="lang-col">
									<h6>Frameworks/ Others</h6>
									<ul>
										<li>React.js</li>
										<li>Next.js</li>
										<li>Node.js</li>
										<li>Boostrap CSS</li>
										<li>Tailwind CSS</li>
									</ul>
								</Col>
							</Row>
					<Row className="desc">
								<p>In a world that changes fast, I adapt with grace,
For they know that success, comes with a steady pace.
A love for the craft, that never fades away,
And the thirst for knowledge, that grows day by day.</p>
							</Row>
						</Col>
				</Row>
				
			</Row>
          
        </Container>
      </div>
	);
};
export default Skill;
