import React,{useState,useEffect} from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

function Navi() {
  const [ActiveLink ,setActiveLink] = useState('home');
  const [scrolled,setScrolled] = useState(false);

  useEffect(() => {
   const onScroll = ()=>{
      if(window.scrollY>50)
      {
        setScrolled (true);
      }else{
        setScrolled(false);
      }
    }

    window.addEventListner("scroll",onScroll);
    return ()=>window.removeEventListner("scroll",onScroll);
  
  }, [])
  
  return (
    <Navbar expand="lg" className={(scrolled)?"scrolled":""}>
    <Container>
      <Navbar.Brand href="#home"><h2>Nidhi Parab</h2></Navbar.Brand>
      <Navbar.Toggle />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto">
          <Nav.Link href="#home" className={ActiveLink === 'home' ? 'active navbar-link':'navbar'}>Home</Nav.Link>
          <Nav.Link href="../skills">Skills</Nav.Link>
        </Nav>
      </Navbar.Collapse>
    </Container>
  </Navbar>
  )
}

export default Navi
