import { Col } from "react-bootstrap";
import './myprojects.css';
import { FaGithub,FaLink} from "react-icons/fa";


export const ProjectCard = ({ title, description,git,web,imgUrl}) => {
    function changeBackground(e) {
        e.target.style.background = 'red';
      }
    function no(e) {
        e.target.style.background = 'w';
      }
  return (
    <Col size={12} sm={6} md={4}>
      {/* <div className="proj-imgbx row align-items-end" style={{ 
      backgroundImage: `url(${imgUrl})` 
    }}> */}
    <button onMouseOver={changeBackground} onMouseLeave={no}>Hover over me!</button>
      <div className="proj-imgbx row align-items-end" >
        <div className="proj-txtx">
          <h4>{title}</h4>
          <span>{description}</span><br/>
          <a href={git} className="icon"><FaGithub size="20px"/></a>
          <a href={web} className={web?'icon':'hide'}><FaLink size="20px"/></a>
        </div>
    
        
      </div>
    </Col>
  )
  }
